import React, { useState } from "react";
import { View, TextInput, Button, Text, TouchableOpacity, StyleSheet, Platform } from "react-native";
import { AntDesign } from "@expo/vector-icons";

const RecuperacaoScreen = ({ navigation }) => {
  const [usuario, setUsuario] = useState("");

  const handleRecuperacao = () => {
    return (
      <View style={styles.container}>
        <AntDesign
          name="back"
          size={24}
          color="black"
          style={styles.backButton}
          onPress={() => navigation.goBack()}
        />
    
        <Text style={styles.title}>
          Informe seu email email ou número de telefone
        </Text>
    
        <TextInput
          placeholder="Email ou número de telefone"
          value={usuario}
          onChangeText={setUsuario}
          style={styles.input}
        />
      
        <TouchableOpacity onPress={handleRecuperacao} style={styles.button}>
          <Text style={styles.text}>Continuar</Text>
        </TouchableOpacity>
    
    
      </View>
    );
  };

};



const styles = StyleSheet.create ({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    paddingHorizontal: 20,
  },

  backButton: {
    alignSelf: "flex-start",
    position: "absolute",
    top: Platform.OS === "ios" ? 40 : 20, // Ajuste para iOS e Android
    left: 10,
  },

  title: {
    fontSize: 22,
    fontWeight: "bold",
    marginTop: 0,
    marginBottom: 100,
    alignSelf: "flex-start",
  },

  input: {
    width: "100%", // Isso faz com que o input ocupe a largura total do container
    height: 57, // Altura do input
    marginBottom: 15, // Espaço entre o input e o botão
    bottom: 50,
    borderWidth: 1,
    borderColor: "#ccc", // Cor da borda do input
    borderRadius: 10, // Bordas arredondadas
    paddingHorizontal: 10, // Padding interno do input
  },
});



export default RecuperacaoScreen
